SELECT NUMERO_ALERTA,DESCRIPCION,STANDARD,FECHA_ULT_EJECUCION,SEGUNDOS_ULT_EJECUCION,GRABAR_LOGFILE,DIAS_LOGFILE,PROCEDIMIENTO_EJECUTAR,OBSERVACIONES,ACTIVA,TAREA_PROGRAMADA,PLANIFICADOR_FRECUENCIA,PLANIFICADOR_ESCALA_TIEMPO,PLANIFICADOR_DESDE_HORA,PLANIFICADOR_HASTA_HORA,PLANIFICADOR_EJECUTAR_EL_DIA,PLANIFICADOR_EJECUTAR_A_HORA,PLANIFICADOR_LUNES,PLANIFICADOR_MARTES,PLANIFICADOR_MIERCOLES,PLANIFICADOR_JUEVES,PLANIFICADOR_VIERNES,PLANIFICADOR_SABADO,PLANIFICADOR_DOMINGO,REARRANCAR_TIEMPO_REAL,INSTANCIA_NODO,ACTIVA_TRAZA,ALERTA_A_EJECUTAR_SI_ERROR,D_ALERTA_A_EJECUTAR_SI_ERROR,ALERTA_A_EJECUTAR_X_TIEMP_EXC,D_ALERTA_A_EJECUTAR_X_TIEMP_EX,SEGUNDOS_ALERTA_X_TIEMP_EXC,ASUNTO_ALERTA,APLICAR_PARSER_ASUNTO,TEXTO_ALERTA,APLICAR_PARSER_TEXTO,APLICAR_PARSER_TEXTO_HTML,TEXTO_ALERTA_SMS,APLICAR_PARSER_TEXTO_SMS,USUARIO_ENVIO,D_USUARIO_ENVIO,CODIGO_SERVIDOR_SMTP,D_CODIGO_SERVIDOR_SMTP,ENVIADA_POR,EMAIL_PRUEBAS,NOTIFICAR_MAIL_ACUSE_RECIBO,ENVIAR_EMAIL,NOTIFICAR_MAIL_ATTACHMENTS,RSRC_GROUP,D_RSRC_GROUP,NLS_NUMERIC_CHARACTERS,ID_COLA_ALERTA_ULTIMA,FECHA_ULT_MODIFICACION,V_VERSION_CF,FECHA_ALTA,USUARIO_ULT_MODIFICACION,USUARIO_ALTA FROM (SELECT ALRT_ALERTAS.* ,DECODE(alrt_alertas.alerta_a_ejecutar_si_error,NULL,NULL,(SELECT alrt.descripcion FROM alrt_alertas alrt WHERE alrt.numero_alerta = alrt_alertas.alerta_a_ejecutar_si_error)) D_ALERTA_A_EJECUTAR_SI_ERROR,DECODE(alrt_alertas.alerta_a_ejecutar_x_tiemp_exc,NULL,NULL,(SELECT alrt.descripcion FROM alrt_alertas alrt WHERE alrt.numero_alerta = alrt_alertas.alerta_a_ejecutar_x_tiemp_exc)) D_ALERTA_A_EJECUTAR_X_TIEMP_EX,DECODE(alrt_alertas.codigo_servidor_smtp,NULL,NULL,(SELECT lvsmtp.descripcion FROM alrt_smtp_servers lvsmtp WHERE lvsmtp.codigo = alrt_alertas.codigo_servidor_smtp)) D_CODIGO_SERVIDOR_SMTP,(SELECT d.comments FROM dba_rsrc_plan_directives d, v$rsrc_plan p WHERE p.name = d.plan AND p.is_top_plan = 'TRUE' AND d.group_or_subplan = alrt_alertas.rsrc_group AND d.status IS NULL) D_RSRC_GROUP,DECODE(alrt_alertas.usuario_envio,NULL,NULL,(SELECT u.descripcion FROM usuarios u WHERE u.usuario=alrt_alertas.usuario_envio)) D_USUARIO_ENVIO,(SELECT fecha_ult_ejecucion FROM alrt_alertas_aux WHERE alrt_alertas.numero_alerta = alrt_alertas_aux.numero_alerta) FECHA_ULT_EJECUCION,(SELECT ID_COLA_ALERTA_ULTIMA FROM ALRT_ALERTAS_AUX WHERE ALRT_ALERTAS.NUMERO_ALERTA = ALRT_ALERTAS_AUX.NUMERO_ALERTA) ID_COLA_ALERTA_ULTIMA,(SELECT segundos_ultima_ejecucion 

FROM alrt_alertas_aux 
WHERE alrt_alertas.numero_alerta = alrt_alertas_aux.numero_alerta) SEGUNDOS_ULT_EJECUCION,NULL V_TEXTO_ALERTA_HTML_CLOB,DECODE(standard, 'S', pkpantallas_aux.decodifica_texto_version_cf(version_cf), NULL) V_VERSION_CF 
FROM ALRT_ALERTAS) ALRT_ALERTAS 
WHERE 8=8/*FILTRO*/ AND (numero_alerta >= 1000) /*FFILTRO*//*INIQRY*/  order by FECHA_ULT_EJECUCION DESC

select numero_alerta, descripcion, ACTIVA, FECHA_ULT_EJECUCION
from alrt_alertas
where activa not in ('N','P')
where numero_alerta in (2012,2021,2000)


UPDATE ALRT_ALERTAS
SET ACTIVA ='P'
where activa not in ('N','P')